import React, { useState, useEffect} from 'react';
import { Link, useParams } from "react-router-dom";
// import { v4 as uuidv4 } from 'uuid';
import axios from "axios";

export const BASE_API = process.env.REACT_APP_BASE_API_URL;
export const USERS_API = `${BASE_API}/api/components`;


const RecipeDetails = ({ match }) => {
  // const { recipeId } = useParams();
  const recipeId='52772'
  const [recipe, setRecipe] = useState(null);
  const [commentContent, setCommentContent] = useState('');
  const [comments, setComments] = useState([]);
  const URL =  "http://localhost:4000/api/comments";
  const URLSaved =  "http://localhost:4000/api/comments/saved";
  const [user, setUser] = useState({
    id: 1,
    username: 'example_user',
    email: 'user@example.com',
    saved: ['52772', 'other_recipe_id'], // 保存的菜谱ID列表
    // 其他用户信息...
  });
  const [isSaved, setIsSaved] = useState(false);

  // const user ={
  //   id: 1,
  //   username: 'example_user',
  //   email: 'user@example.com',
  //   saved: ['52772', 'other_recipe_id'], // 保存的菜谱ID列表
  //   // 其他用户信息...
  // }

  const getCommentsForRecipe = async (recipeId) => {
    const response = await axios.get(`${URL}/${recipeId}`);
    return response.data;
  };

  const getSavedRecipe = async (userId) => {
    const response = await axios.get(`${URLSaved}/${userId}`);
    return response.data;
  };

//   const createComment = async (commentData) => {
//     const response = await axios.post(URL, commentData);
//     // return response.data;
//     return response.status;
// };
  const createComment = async (commentData) => {
    try {
      const response = await axios.post(URL, commentData);
      return response.status === 200; // 返回一个布尔值，表示请求是否成功
    } catch (error) {
      console.error('Failed to post comment:', error);
      return false; // 请求失败，返回 false
    }
};

const formatDate = (dateString) => {
  return dateString.substring(0, 10); // 截取日期部分，保留年月日
};


  useEffect(() => {
    const fetchRecipeAndComments = async () => {
      try {
        // 获取菜谱信息
        const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${recipeId}`);
        const data = await response.json();
        setRecipe(data.meals[0]);

        const recipeSaved = isRecipeSaved(); // 检查菜谱是否已保存
        // const userId = user.id
        // const savedRecipe = await getSavedRecipe(userId);
        // const isRecipeSaved = savedRecipe.includes(recipeId);
        // console.log(savedRecipe)
        // console.log(isRecipeSaved); // 如果包含则为 true，否则为 false

        setIsSaved(recipeSaved); // 设置isSaved状态
        // 获取评论信息
        console.log(recipeId)
        const commentsData = await getCommentsForRecipe(recipeId);
        console.log("fetch1")
        console.log(commentsData.comments)
        setComments(commentsData.comments);
        // console.log(comments)

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };
    fetchRecipeAndComments();
  },  [recipeId,isSaved]);


  const handlePostComment = async () =>{
    const userId = user.id; // 获取用户ID
    // const commentId = uuidv4(); // 生成评论的唯一ID
    const commentData = {
        // commentId: commentId,
        userId: userId,
        recipeId: recipeId, // 使用你的recipeId变量
        commentContent: commentContent,
        time: new Date().toISOString(), // 获取当前时间
    };
    try {
        const response = await createComment(commentData)
        console.log("this is response"+response)
        if (response) {
        // 在此处处理成功发送评论后的逻辑
        // 例如清除输入框中的内容，更新评论列表等
        setCommentContent(''); // 清空评论内容
        // 更新评论列表等操作
        setComments([...comments,commentData])
        } else {
          // 处理请求失败的情况
          console.error('Failed to post comment');
        }
      } catch (error) {
        console.error('Error posting comment:', error);
      }
  };

  const isRecipeSaved = async () =>{
    try {
      const userId = user.id;

      // console.log(userId)
      const savedRecipe = await getSavedRecipe(userId);
      console.log("isRecipeSaved");
      console.log(savedRecipe);
      const isRecipeSaved = savedRecipe.user.saveRecipe.includes(recipeId);

      console.log(isRecipeSaved); // 如果包含则为 true，否则为 false
      return isRecipeSaved;
    } catch (error) {
      console.error('Error fetching savedRecipe:', error);
    }

  };

  // const isRecipeSaved_tep = () => {
  //   if (user&& user.saved) {

  //     if (user.saved.includes(recipeId)){
  //       return true;
  //     }
  //   }
  //   return false;
  // };

  const handleSave = () => {
    if (user) {
      if (isSaved) {
        // 执行取消保存的逻辑，从 SaveLists 表中删除保存项
        console.log('Unsave recipe');
        // set current user with new saved
        // const updatedSaved = user.saved.filter(id => id !== recipeId);
        // setUser({ ...user, saved: updatedSaved })


        console.log(user)
        //使用 fetch 或其他适当的方法向后端发送取消保存请求
        setIsSaved(!isSaved);

      } else {
        // 执行保存的逻辑，将保存项添加到 SaveLists 表中
        const updatedSaved = [...user.saved, recipeId];
        setUser(prevUser => ({ ...prevUser, saved: updatedSaved }));
        // setUser({ ...user, saved: updatedSaved });
        setUser({ ...user, saved: updatedSaved })
        // 使用 fetch 或其他适当的方法向后端发送保存请求
        setIsSaved(!isSaved);
      }
    } else {
      console.log('User is not logged in');
      // 在这里触发登录界面或者显示提示信息
    }
  };
  // console.log("t1")
  // console.log(Object.values(comments))

  return (
    <div >
    <div className="container mt-4" >
      {recipe ? (
        <div className="row">
          <div className="col-md-6 mb-4">
            <img src={recipe.strMealThumb} alt={recipe.strMeal} className="img-fluid" />
          </div>
          <div className="col-md-6">
            <h2>{recipe.strMeal}</h2>
            <p>{recipe.strArea} | {recipe.strTags}</p>
            <h4>Ingredients</h4>
            <ul>
              {Object.keys(recipe)
                .filter(key => key.includes('strIngredient') && recipe[key])
                .map((ingredientKey, index) => (
                  <li key={index}>{recipe[ingredientKey]} - {recipe[`strMeasure${ingredientKey.slice(13)}`]}</li>
                ))}
            </ul>
            <h4>Instructions</h4>
            <p>{recipe.strInstructions}</p>
          </div>
        </div>
      ) : (
        <p>Loading recipe...</p>
      )}
       {/* 保存或取消保存按钮 */}
       {user && (
        <div className="mb-4">
          <button className="btn btn-primary" onClick={handleSave} style={{ backgroundColor:'black'}}>
            {isSaved ? 'Unsave' : 'Save'}
          </button>
        </div>
      )}

      {user && (
        <div className="mb-4">
          <h4>Comments</h4>
          <textarea
            rows="4"
            className="form-control mb-2"
            placeholder="Write your comment here..."
            value={commentContent}
            onChange={(e) => setCommentContent(e.target.value)}
          ></textarea>
          <button className="btn btn-primary" onClick={handlePostComment} style={{ backgroundColor:'black'}}>
            Post
          </button>
        </div>
      )}


      {/* 显示已有的评论 */}
      <div className="mb-4">
        <ul style={{listStyle:'none'}}>
          {/* {Object.entries(comments).map(([key, value], index) => (
  <li key={index}>
  <div style={{ display: 'flex', flexDirection: 'column' }}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            {key === 'userId' && (
              <Link to={`/Profile/${value}`}>
                <span style={{ marginRight: '10px' }}>{value}</span>
              </Link>
            )}
            {key === 'time' && (
              <span>{value}</span>
            )}
          </div>
          {key === 'commentContent' && (
            <div>{value}</div>
          )}
        </div>
      </li>
))} */}
{comments.map((comment, index) => (
      <li key={index}>
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <Link to={`/Profile/${comment.userId}`}>
              <span style={{ marginRight: '10px' }}>{comment.userId}</span>
            </Link>
            <span>{formatDate(comment.time)}</span>
          </div>
          <p>{comment.commentContent}</p>
        </div>
      </li>
    ))}
        </ul>
      </div>
    </div>
    </div>
  );
};

export default RecipeDetails;
