import React, { useState, useEffect } from 'react';

const RecipeDetails = ({ match }) => {
  const uniqueIdentifier = '52772';
  const [recipe, setRecipe] = useState(null);
  const [commentContent, setCommentContent] = useState('');
  const [comments, setComments] = useState([]);
  const [user, setUser] = useState({}); // 用于存储用户信息，包括保存的食谱列表

  useEffect(() => {
    const fetchRecipeAndComments = async () => {
      try {
        // 获取菜谱信息
        const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${uniqueIdentifier}`);
        const data = await response.json();
        setRecipe(data.meals[0]);

        // 获取评论信息
        const commentsResponse = await fetch(`backend_url/comments/${uniqueIdentifier}`);
        const commentsData = await commentsResponse.json();
        setComments(commentsData.comments);

        // 获取用户信息，包括保存的食谱列表
        const userResponse = await fetch('backend_url/user'); // 获取用户信息的 API 路径
        const userData = await userResponse.json();
        setUser(userData); // 设置用户信息

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchRecipeAndComments(); // 调用同时获取食谱和评论的函数
  }, [uniqueIdentifier]);


  const isLoggedIn = () => {
    return true; // 替换为你的实际登录逻辑
  };

  const handlePostComment = () => {
    // ... 省略了发送评论到后端的代码
  };

  const isRecipeSaved = () => {
    if (user.attributes && user.attributes.saved) {
      return user.attributes.saved.includes(uniqueIdentifier);
    }
    return false;
  };

  const handleSave = () => {
    if (isLoggedIn()) {
      if (isRecipeSaved()) {
        // 执行取消保存的逻辑，从 SaveLists 表中删除保存项
        console.log('Unsave recipe');
        // 使用 fetch 或其他适当的方法向后端发送取消保存请求
      } else {
        // 执行保存的逻辑，将保存项添加到 SaveLists 表中
        const saveObject = {
          saveId: 'save_id_here',
          userId: 'user_id_here',
          recipeId: uniqueIdentifier,
          time: new Date().toISOString(),
        };
        console.log('Save recipe:', saveObject);
        // 使用 fetch 或其他适当的方法向后端发送保存请求
      }
    } else {
      console.log('User is not logged in');
      // 在这里触发登录界面或者显示提示信息
    }
  };

  return (
    <div className="container mt-4">
      {recipe ? (
        <div className="row">
          <div className="col-md-6 mb-4">
            <img src={recipe.strMealThumb} alt={recipe.strMeal} className="img-fluid" />
          </div>
          <div className="col-md-6">
            <h2>{recipe.strMeal}</h2>
            <p>{recipe.strArea} | {recipe.strTags}</p>
            <h4>Ingredients</h4>
            <ul>
              {Object.keys(recipe)
                .filter(key => key.includes('strIngredient') && recipe[key])
                .map((ingredientKey, index) => (
                  <li key={index}>{recipe[ingredientKey]} - {recipe[`strMeasure${ingredientKey.slice(13)}`]}</li>
                ))}
            </ul>
            <h4>Instructions</h4>
            <p>{recipe.strInstructions}</p>
          </div>
        </div>
      ) : (
        <p>Loading recipe...</p>
      )}
       {/* 保存或取消保存按钮 */}
       {isLoggedIn() && (
        <div className="mb-4">
          <button className="btn btn-primary" onClick={handleSave}>
            {isRecipeSaved() ? 'Unsave' : 'Save'}
          </button>
        </div>
      )}

      {isLoggedIn() && (
        <div className="mb-4">
          <h4>Comments</h4>
          <textarea
            rows="4"
            className="form-control mb-2"
            placeholder="Write your comment here..."
            value={commentContent}
            onChange={(e) => setCommentContent(e.target.value)}
          ></textarea>
          <button className="btn btn-primary" onClick={handlePostComment}>
            Post
          </button>
        </div>
      )}


      {/* 显示已有的评论 */}
      <div className="mb-4">
        <h4>Comments</h4>
        <ul>
          {comments.map((comment, index) => (
            <li key={index}>
              <p>User ID: {comment.userId}</p>
              <p>Comment: {comment.commentContent}</p>
              <p>Time: {comment.time}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecipeDetails;
