import React, { useState, useEffect } from 'react';

const RecipeDetails = ({ match }) => {
  const uniqueIdentifier = '52772';
  const [recipe, setRecipe] = useState(null);
  const [commentContent, setCommentContent] = useState('');

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${uniqueIdentifier}`);
        const data = await response.json();
        setRecipe(data.meals[0]);
      } catch (error) {
        console.error('Error fetching recipe:', error);
      }
    };

    fetchRecipe();
  }, [uniqueIdentifier]);

  const isLoggedIn = () => {
    return true; // Replace with your actual login logic
  };

  const handlePostComment = () => {
    if (isLoggedIn()) {
      const commentObject = {
        userId: 'user_id_here',
        commentContent,
        time: new Date().toISOString(),
        receiptsId: uniqueIdentifier,
      };
      console.log('Post comment:', commentObject);
      // Send 'commentObject' to the backend logic

      // You can perform further operations after sending the comment
    } else {
      console.log('User is not logged in');
      // Handle the scenario for non-logged in users
    }
  };

  return (
    <div className="container mt-4">
      {recipe ? (
        <div className="row">
          <div className="col-md-6 mb-4">
            <img src={recipe.strMealThumb} alt={recipe.strMeal} className="img-fluid" />
          </div>
          <div className="col-md-6">
            <h2>{recipe.strMeal}</h2>
            <p>{recipe.strArea} | {recipe.strTags}</p>
            <h4>Ingredients</h4>
            <ul>
              {Object.keys(recipe)
                .filter(key => key.includes('strIngredient') && recipe[key])
                .map((ingredientKey, index) => (
                  <li key={index}>{recipe[ingredientKey]} - {recipe[`strMeasure${ingredientKey.slice(13)}`]}</li>
                ))}
            </ul>
            <h4>Instructions</h4>
            <p>{recipe.strInstructions}</p>
            {/* Other information */}
          </div>
        </div>
      ) : (
        <p>Loading recipe...</p>
      )}
      {isLoggedIn() && (
        <div className="mb-4">
          <h4>Comments</h4>
          <textarea
            rows="4"
            className="form-control mb-2"
            placeholder="Write your comment here..."
            value={commentContent}
            onChange={(e) => setCommentContent(e.target.value)}
          ></textarea>
          <button className="btn btn-primary" onClick={handlePostComment}>
            Post
          </button>
        </div>
      )}
    </div>
  );
};

export default RecipeDetails;
